import { useEffect} from 'react'

let windowWidth: number

const rnd = (m:number,b:number)=>(Math.floor( Math.random() * (n - m + 1) ) + m)
export default function Confetti() {

    useEffect(()=>{
        if(window){
            windowWidth = window.innerWidth
        }
    },[])

    const confetti = (i:number)  =>{
       return (<span key={i} className={`particle c${rnd([1,2])}`} style={{top:Math.random(), left:Math.random(), width: Math.random()+'px', height: Math.random() + 'px', animationDelay: Math.random + 's'}}></span>)
}

    return (
        <div className='confetti'>
           {Array.from({length: (windowWidth/50)*10}, (item, i) => confetti(i))}
        </div>)
 }
