import { useEffect, useState } from 'react'

export default function useUtils (){

    const [windowWidth, setWindowWidth ] =useState<number|null>(null)

    useEffect(()=>{
        if(window){
            setWindowWidth(window.innerWidth)
        }
    },[])

    return {windowWidth}

}
