import React, { useContext, useState, useEffect } from 'react'


import {GameContext} from '../context/GameContext'
import Guess from '../types/Guess'


export default function GuessRow ({guess, guessNumber, currentRound}:{guess:Guess, currentRound:number, guessNumber: (value:string[])=>void}){
  const { digits, giveHint, hintsGiven} = useContext(GameContext)
  const {round, result, value, passed} = guess
  const [inputValue, setInputValue]=useState<string[]>(value)
  const inputRef = React.createRef<HTMLInputElement>()
  let hintGiven = false


    useEffect(()=>{
      if(!guess.passed){
        setInputValue(hintsGiven)
      }
    },[hintsGiven, guess])

    const handleUserKeyPress = (event:KeyboardEvent) => {
      const { keyCode } = event
      if (keyCode === 13 && !passed && inputValue.length === digits) {
        guessNumber(inputValue)
      }
    }

    useEffect(() => {
      window.addEventListener("keydown", handleUserKeyPress);

      return () => {
        window.removeEventListener("keydown", handleUserKeyPress);
      };
    }, [handleUserKeyPress])

    const handleChange = (value: string)=>{
      value = value.replace(/\D/g, '')
      setInputValue(Array.from(value))
    }

    const handleClick =()=>{
      if(inputValue.length !== digits) return false
      guessNumber(inputValue)
    }

    const onHintClick = ()=>{
      hintGiven = true
      giveHint()
    }

    const id=`guessInput${round}`
    const showHint = !hintGiven && [2,5,8].includes(currentRound) && hintsGiven.length>0

    return (
      <div className='flex flex-col sm:flex-row mx-auto mb-6'>
          <div className='flex'>
          <div className='w-56 mr-4'>
            <input
              autoFocus
              autoComplete='off'
              ref={inputRef}
              className='nes-input focus:outline-none text-center custom-padding'
              disabled={passed}
              type='tel'
              placeholder={Array.from({length: digits}, (item)=>'X').join(' ')}
              id={id}
              value={inputValue.join(' ')}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) => handleChange(event.target.value)}
              maxLength={digits*2-1}
            />
          </div>
          {!passed && <div className='flex flex-row'>
            <img src='press.png' className="ml-2 w-auto h-12" style={{imageRendering: 'pixelated'}} onClick={handleClick}></img>

            {showHint &&  <img src='hint.png' className="ml-2 w-auto h-10 self-center animateOpacity" style={{imageRendering: 'pixelated'}} onClick={onHintClick}></img>}
            </div>}
          </div>
          {passed &&
            <div className='text-left flex flex-col sm:flex-row mt-2 lg:mt-0'>
              {result.missed && <div className='self-center nes-badge wide'><span className="is-error">Ups! you missed it!</span></div>}
              {result.plus && <div className='self-center nes-badge plus'><span className="is-success">{`${result.plus} cuk!`}</span></div>}
              {result.minus && <div className='self-center nes-badge wide'><span className="is-warning">{`${result.minus} position wrong!`}</span></div>}
            </div>}
      </div>)
}
