import React, { useContext } from 'react'


import {GameContext} from '../context/GameContext'

export default function Hint (){
    const { giveHint} = useContext(GameContext)

    return <div className='flex justify'>
         <span className='ml-2 self-center'>Do you need a hint?</span>
         <img src='press.png' className="ml-2 w-auto h-12" style={{imageRendering: 'pixelated'}} onClick={giveHint}></img>
    </div>
}
